<?php/** * Class Woo_TRLS_Order * Class for display order arrows template * * @since 2.1.8 */class Woo_TRLS_Order {	/**	 * List of meta fields for orderby	 *	 * @since 2.1.8	 *	 * @var array	 */	public static $meta_fields = array(		'woo_tradeline_limit',		'woo_tradeline_month',		'woo_tradeline_softpull',		'woo_tradeline_report',		'_stock',		'_price',	);	/**	 * Show order arrows	 *	 * @since 2.1.8	 *	 * @param $title string Title for column	 * @param $orderby string Order by	 */	public static function order_link( $title, $orderby ) {		$current_column = isset( $_GET['trls_orderby'] ) && $_GET['trls_orderby'] == $orderby;		$asc            = isset( $_GET['trls_order'] ) && 'ASC' == $_GET['trls_order'];		$link = esc_url( add_query_arg(			array(				'trls_orderby' => $orderby,				'trls_order'   => $asc && $current_column ? 'DESC' : 'ASC',			), $_SERVER['REQUEST_URI'] ) );		$order_type_class = '';		if ( $current_column ) {			$order_type_class = $asc ? ' asc' : ' desc';		}		echo '<a href="' . $link . '" class="woo-trls-order' . $order_type_class . '">' . $title . '</a>';			}}